import { useState } from "react";
import {
  Keyboard,
  Modal,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  TouchableWithoutFeedback,
  View,
} from "react-native";
import FilterTab from "../../components/FilterTab";
import Reservation from "../../components/Reservation";
import ServicesList from "../../components/ServicesList";
import SpecialistSection from "../../components/SpecialistSection";

import type { StackNavigationProp } from "@react-navigation/stack";

// Images spécifiques pour chaque service
const allServices = [
  {
    id: 1,
    title: "Coupe homme",
    subtitle: "Rapide et stylée",
    imageUrl: {
      uri: "data:image/jpeg;base64,",
    },
    hasArrow: true,
    hasCircle: false,
    category: "Coupes",
  },
  {
    id: 2,
    title: "Soin visage express",
    subtitle: "Pour une peau éclatante",
    imageUrl: {
      uri: "data:image/jpeg;base64,",
    },
    hasArrow: true,
    hasCircle: false,
    category: "Soin du visage",
  },
  {
    id: 3,
    title: "Henné mains",
    subtitle: "Art traditionnel et élégant",
    imageUrl: {
      uri: "data:image/jpeg;base64,",
    },
    hasArrow: true,
    hasCircle: false,
    category: "Henné",
  },
  {
    id: 4,
    title: "Massage relaxant",
    subtitle: "Détente et bien-être",
    imageUrl: {
      uri: "data:image/jpeg;base64,",
    },
    hasArrow: true,
    hasCircle: false,
    category: "Massage",
  },
  {
    id: 5,
    title: "Coupe enfant",
    subtitle: "Fun et adaptée aux petits",
    imageUrl: {
      uri: "data:image/jpeg;base64,",
    },
    hasArrow: true,
    hasCircle: false,
    category: "Coupes",
  },
  
  

  // Ajoute d'autres services ici si besoin
];

type ServicesScreenProps = {
  navigation: StackNavigationProp<any>;
};

type ServiceType = {
  id: number;
  title: string;
  subtitle: string;
  imageUrl: any;
  hasArrow: boolean;
  hasCircle: boolean;
  category: string;
};

const ServicesScreen: React.FC<ServicesScreenProps> = () => {
  const [activeFilter, setActiveFilter] = useState("Tous");
  const [search, setSearch] = useState("");
  const [selectedService, setSelectedService] = useState<ServiceType | null>(
    null
  );
  const [showReservation, setShowReservation] = useState(false);

  // Filtres en français
  const filters = ["Tous", "Coupes", "Soin du visage", "Henné", "Massage"];

  // Filtrage dynamique par catégorie puis par recherche
  const filteredServices = allServices
    .filter((service) =>
      activeFilter === "Tous" ? true : service.category === activeFilter
    )
    .filter((service) =>
      service.title.toLowerCase().includes(search.trim().toLowerCase())
    );

  return (
    <SafeAreaView style={styles.container}>
      <TouchableWithoutFeedback onPress={Keyboard.dismiss} accessible={false}>
        <View style={{ flex: 1 }}>
          {/* Barre de recherche améliorée */}
          <View style={styles.searchBarContainer}>
            <View style={styles.searchBar}>
              <Ionicons name="search-outline" size={20} color="#888888" />
              <TextInput
                style={styles.searchInput}
                placeholder="Rechercher un service..."
                placeholderTextColor="#888888"
                value={search}
                onChangeText={setSearch}
                returnKeyType="search"
                clearButtonMode="while-editing"
                accessibilityLabel="Rechercher un service"
              />
              {search.length > 0 && (
                <TouchableOpacity
                  onPress={() => setSearch("")}
                  style={styles.clearButton}
                  accessibilityLabel="Effacer la recherche"
                >
                  <Ionicons name="close-circle" size={18} color="#888888" />
                </TouchableOpacity>
              )}
            </View>
          </View>

          {/* Filtres de catégorie */}
          <ScrollView
            horizontal
            showsHorizontalScrollIndicator={false}
            style={styles.filterContainer}
            contentContainerStyle={styles.filterContent}
          >
            {filters.map((filter, index) => (
              <FilterTab
                key={index}
                title={filter}
                isActive={filter === activeFilter}
                onPress={() => setActiveFilter(filter)}
              />
            ))}
          </ScrollView>

          {/* Liste des spécialistes et services */}
          <ScrollView
            showsVerticalScrollIndicator={false}
            keyboardShouldPersistTaps="handled"
          >
            <SpecialistSection />
            <ServicesList
              services={filteredServices}
              onServicePress={(service: ServiceType) => {
                setSelectedService(service);
                setShowReservation(true);
              }}
            />
            {filteredServices.length === 0 && (
              <Text style={styles.emptyText}>Aucun service trouvé.</Text>
            )}
          </ScrollView>

          {/* Modale de réservation */}
          <Modal
            visible={showReservation}
            animationType="slide"
            onRequestClose={() => setShowReservation(false)}
          >
            {selectedService && (
              <Reservation
                service={selectedService}
                onClose={() => setShowReservation(false)}
              />
            )}
          </Modal>
        </View>
      </TouchableWithoutFeedback>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  searchBarContainer: {
    flexDirection: "row",
    paddingHorizontal: 16,
    paddingVertical: 12,
    alignItems: "center",
  },
  searchBar: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#F5F5F5",
    borderRadius: 8,
    paddingHorizontal: 12,
    paddingVertical: 8,
  },
  searchInput: {
    flex: 1,
    marginLeft: 8,
    fontSize: 16,
  },
  clearButton: {
    marginLeft: 4,
    padding: 2,
  },
  filterContainer: {
    maxHeight: 50,
  },
  filterContent: {
    paddingHorizontal: 16,
  },
  emptyText: {
    textAlign: "center",
    color: "#888",
    marginTop: 32,
    fontSize: 16,
  },
});

export default ServicesScreen;
